import React from 'react';
import { Layout, Typography, Button, Row, Col, Form, Input } from 'antd';

const { Content } = Layout;
const { Title, Paragraph } = Typography;

const LandingPage = () => {
  const onFinishLogin = (values) => {
    console.log('Login form values:', values);
  };

  const onFinishSignup = (values) => {
    console.log('Signup form values:', values);
  };

  return (
    <Layout>
      <Content style={{ padding: '50px', textAlign: 'center' }}>
        <Title level={2}>Welcome to Our Training Center</Title>
        <Paragraph>
          We offer a wide range of courses to enhance your skills and knowledge in various fields.
        </Paragraph>
        <Button type="primary" size="large">
          Explore Courses
        </Button>
        <Row gutter={[16, 16]} justify="center">
          <Col span={8}>
            <Title level={3}>Login</Title>
            <Form name="login" onFinish={onFinishLogin}>
              <Form.Item
                label="Email"
                name="email"
                rules={[
                  { required: true, message: 'Please enter your email address' },
                  { type: 'email', message: 'Please enter a valid email address' },
                ]}
              >
                <Input />
              </Form.Item>

              <Form.Item
                label="Password"
                name="password"
                rules={[{ required: true, message: 'Please enter your password' }]}
              >
                <Input.Password />
              </Form.Item>

              <Form.Item>
                <Button type="primary" htmlType="submit">Log in</Button>
              </Form.Item>
            </Form>
          </Col>
          <Col span={8}>
            <Title level={3}>Sign Up</Title>
            <Form name="signup" onFinish={onFinishSignup}>
              <Form.Item
                label="Name"
                name="name"
                rules={[{ required: true, message: 'Please enter your name' }]}
              >
                <Input />
              </Form.Item>

              <Form.Item
                label="Email"
                name="email"
                rules={[
                  { required: true, message: 'Please enter your email address' },
                  { type: 'email', message: 'Please enter a valid email address' },
                ]}
              >
                <Input />
              </Form.Item>

              <Form.Item
                label="Password"
                name="password"
                rules={[{ required: true, message: 'Please enter your password' }]}
              >
                <Input.Password />
              </Form.Item>

              <Form.Item>
                <Button type="primary" htmlType="submit">Sign Up</Button>
              </Form.Item>
            </Form>
          </Col>
        </Row>
      </Content>
    </Layout>
  );
};

export default LandingPage;
