import React, { useState } from 'react';
import { Form, Input, Button, Typography } from 'antd';
import "../styles/signup.css";

const SignUp = () => {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordMatchError, setPasswordMatchError] = useState(false);

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
    setPasswordMatchError(e.target.value !== password);
  };

  const onFinish = (values) => {
    console.log('Form values:', values);
  };

  return (
    <div className="inscrireinfo">
      <Form name="signup-form" onFinish={onFinish}>
        <Typography.Title>Sign Up</Typography.Title>
        <Form.Item
          label="Email"
          name="email"
          rules={[
            { required: true, message: 'Please enter your email' },
            { type: 'email', message: 'Please enter a valid email' },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Password"
          name="password"
          rules={[{ required: true, message: 'Please enter your password' },
          { min: 6, message: 'Password must be at least 6 characters' }]}
        >
          <Input.Password onChange={handlePasswordChange} />
        </Form.Item>
        <Form.Item
          label="Confirm Password"
          name="confirmPassword"
          rules={[
            { required: true, message: 'Please confirm your password' },
            ({ getFieldValue }) => ({
              validator(_, value) {
                if (!value || getFieldValue('password') === value) {
                  return Promise.resolve();
                }
                return Promise.reject('Passwords do not match');
              },
            }),
          ]}
        >
          <Input.Password onChange={handleConfirmPasswordChange} />
          {passwordMatchError && (
            <Typography.Text type="danger">Passwords do not match</Typography.Text>
          )}
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Sign Up
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default SignUp;
