import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { Button } from 'antd';
import { SettingOutlined } from '@ant-design/icons';
import { AuthContext } from '../AuthContext/AuthContext';
import '../styles/Navbar.css';
import Logo from '../images/logo1.png';

function Navbar() {
  const { isLoggedIn, setIsLoggedIn } = useContext(AuthContext);
  const [showSettingsIcon, setShowSettingsIcon] = useState(false); // State for icon visibility

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowSettingsIcon(false); // Hide the "Paramètre" icon after logout
  };

  const handleLogin = () => {
    setShowSettingsIcon(true); // Show the "Paramètre" icon after login
    navigate('/login'); // Navigate to the login page
  };

  return (
    <div className="navbar">
      <div className="leftSide">
        <img src={Logo} alt="Logo" />
      </div>
      <div className="rightSide">
        <Link to="/">Home</Link>
        <Link to="/Courses">Our Training Courses</Link>
        <Link to="/About">About us</Link>
        <div className="loginButton">
          {isLoggedIn ? (
            <>
              <button onClick={handleLogout}>Logout</button>
              {showSettingsIcon && (
                <Link to="/settings">
                  <SettingOutlined />
                </Link>
              )}
            </>
          ) : (
            <>
              <Button onClick={handleLogin}>Login</Button>
              {/* The Paramètre icon is rendered here based on showSettingsIcon */}
              {showSettingsIcon && (
                <Link to="/settings">
                  <SettingOutlined />
                </Link>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Navbar;
