import React, { useContext, useState } from 'react';
import { Form, Input, Button, Typography } from 'antd';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext/AuthContext';
import { SettingOutlined } from '@ant-design/icons'; // Import the SettingOutlined icon
import '../styles/Login.css';

const Login = () => {
  const { setIsLoggedIn } = useContext(AuthContext);
  const navigate = useNavigate();
  const [showSettingsIcon, setShowSettingsIcon] = useState(false); // State for icon visibility

  const onFinish = (values) => {
    console.log('Form values:', values);
    setIsLoggedIn(true);
    setShowSettingsIcon(true); // Show the "Paramètre" icon after login
  };

  const handleSignUpClick = () => {
    navigate('/Signup/index');
  };

  return (
    <div className="loginfo">
      <Form name="login-form" onFinish={onFinish}>
        <Typography.Title>Login</Typography.Title>
        <Form.Item
          label="Email"
          name="email"
          rules={[{ required: true, message: 'Ce champ est obligatoire' }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Password"
          name="password"
          rules={[{ required: true, message: 'Ce champ est obligatoire' }]}
        >
          <Input.Password />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Login
          </Button>
          <Button type="primary" onClick={handleSignUpClick}>
            SignUp
          </Button>
          {showSettingsIcon && (
            <Button type="primary" onClick={() => navigate('/settings')}>
              <SettingOutlined /> Paramètre
            </Button>
          )}
        </Form.Item>
      </Form>
    </div>
  );
};

export default Login;
