import React, { useState } from 'react';
import { Layout, Typography, Button, Row, Col } from 'antd';
import LoginForm from '../Login'
import SignupForm from '../Signup';

const { Header, Content, Footer } = Layout;
const { Title, Paragraph } = Typography;

const LandingPage = () => {
  const [showSignupForm, setShowSignupForm] = useState(false);

  const onFinishLogin = (values) => {
    console.log('Login form values:', values);
  };

  const onFinishSignup = (values) => {
    console.log('Signup form values:', values);
  };

  const handleSignupClick = () => {
    setShowSignupForm(true);
  };

  return (
    <Layout>
      {/* Header / En-tête */}
      <Header style={{ background: '#fff', padding: '10px', textAlign: 'center' }}>
        {/* Insérez ici votre logo ou le texte du nom */}
        <Title level={3}>Mon Logo / Nom</Title>
        <Button type="link" onClick={() => console.log('About Us clicked')}>
          About Us
        </Button>
      </Header>

      {/* Contenu principal de la landing page */}
      <Content style={{ padding: '50px', textAlign: 'center' }}>
        <Title level={2}>Welcome to Our Training Center</Title>
        <Paragraph>
          We offer a wide range of courses to enhance your skills and knowledge in various fields.
        </Paragraph>
        {showSignupForm ? (
          <Row gutter={[16, 16]} justify="center">
            <Col span={8}>
              <SignupForm onFinish={onFinishSignup} />
            </Col>
          </Row>
        ) : (
          <Row gutter={[16, 16]} justify="center">
            <Col span={8}>
              <LoginForm onFinish={onFinishLogin} />
              <Button type="link" onClick={handleSignupClick}>
                Signup
              </Button>
            </Col>
          </Row>
        )}
      </Content>

      {/* Footer / Pied de page */}
      <Footer style={{ background: '#f9f9f9', textAlign: 'center', padding: '10px' }}>
        {/* Insérez ici le contenu de votre footer */}
        Footer content here
      </Footer>
    </Layout>
  );
};

export default LandingPage;
